const start = flow.get("start")
const end = flow.get("end")
const project_id = flow.get("project_id")
const description = flow.get("description")

if (description !== "") {
    msg.query = `insert into tbltime ("project_id", "start", "end", "description")
    values (${project_id}, to_timestamp(${start} / 1000), to_timestamp(${end} / 1000), '${description}')`
} else {
    msg.query = `insert into tbltime ("project_id", "start", "end")
    values (${project_id}, to_timestamp(${start} / 1000), to_timestamp(${end} / 1000))`
}


return msg;

const inp = msg.payload;

var options = []

for (let option of inp) {
    options.push({[option["name"]]: parseInt(option["id"])})
}

msg.options = options

msg.payload = Object.values(options[0])[0]

return msg;

const inp = msg.payload;

for (let i in inp) {
    // Time
    inp[i]["start"] = inp[i]["start"].toLocaleString("de-CH")
    inp[i]["end"] = inp[i]["end"].toLocaleString("de-CH")

    // Working Time
    inp[i]["working_time"] = parseFloat(inp[i]["working_time"])
}

return msg;

const inp = msg.payload;

for (let i in inp) {
    // Working Time
    inp[i]["time"] = parseFloat(inp[i]["time"])
}

return msg;

const filter = flow.get("filter")

// Query without filter
msg.query = `
select tbltime.id, tblprojects.name as "project_name", "start", "end", extract(epoch from "end"-"start") / 3600 as "working_time", extract (week from start) as "calendar_week", tbltime.description
from tbltime
left join tblprojects on tblprojects.id = tbltime.project_id`

if (filter === undefined) {
    return msg;
}

if (filter["calendar_week"] === null) {
    return msg;
}

msg.query = `
select tbltime.id, tblprojects.name as "project_name", "start", "end", extract(epoch from "end"-"start") / 3600 as "working_time", extract (week from start) as "calendar_week", tbltime.description
from tbltime
left join tblprojects on tblprojects.id = tbltime.project_id
where extract (week from start) = ${filter["calendar_week"]}`

return msg;

const grouping = flow.get("grouping")
msg.query = `
select calendar_week AS KW, sum(working_time) AS Time
from (
    select tbltime.id, tblprojects.name as "project_name", "start", "end", extract(epoch from "end"-"start") / 3600 as "working_time", extract (week from start) as "calendar_week"
    from tbltime left join tblprojects on tblprojects.id = tbltime.project_id
)
group by calendar_week order by calendar_week DESC
`

if (grouping === 1) {
    msg.query = `
    select "day_of_year" AS DOY, sum("working_time") AS Time
    from (
        select tbltime.id, tblprojects.name as "project_name", "start", "end", extract(epoch from "end"-"start") / 3600 as "working_time", extract (DOY from start) as "day_of_year"
        from tbltime left join tblprojects on tblprojects.id = tbltime.project_id
    )
    group by "day_of_year" order by "day_of_year" DESC
    `
}

if (grouping === 2) {
    msg.query = `
    select "project_name", sum("working_time") AS Time
    from (
        select tbltime.id, tblprojects.name as "project_name", "start", "end", extract(epoch from "end"-"start") / 3600 as "working_time", extract (DOY from start) as "day_of_year"
        from tbltime left join tblprojects on tblprojects.id = tbltime.project_id
    )
    group by "project_name"
    `
}

return msg;

const inp = msg.payload;

if (!("kw" in inp)) {
    return null;
}

msg.url = `http://host.docker.internal:8000/time/project?calendar_week=${inp.kw}`

return msg;

const project_name = flow.get("project_name")
const project_number = flow.get("project_number")

msg.payload = {"name":project_name,"project_nr":project_number}

return msg;

const inp = msg.payload

const system_prompt = `
The user will give you a list of tasks he finished in the last working time period.
Return a short sentence summary of what the did.
Do not add any additional information or try to define any word or topic.
`

msg.payload = {
    "model": "google/gemma-3n-e4b",
    "messages": [ 
      { "role": "system", "content": system_prompt},
      { "role": "user", "content": inp.join('\n')}
    ], 
    "temperature": 0.1, 
    "max_tokens": -1,
    "stream": false
  };
msg.headers = {};
msg.headers['Content-Type'] = 'application/json';
return msg;

const inp = msg.payload.choices[0].message.content

function extractNonThinkingMessage(text) {
  // Removes the <think>...</think> tags and their content
  return text.replace(/<think>[\s\S]*?<\/think>/g, '').trim();
}

msg.payload = extractNonThinkingMessage(inp)
return msg;

const start = flow.get("start") || null
const end = flow.get("end") || null

if (start !== null && end !== null) {
    msg.url = `http://host.docker.internal:8000/time/?start_date=${new Date(start).toISOString().split('T')[0]}&end_date=${new Date(end).toISOString().split('T')[0]}`
    return msg;
}

if (start !== null) {
    msg.url = `http://host.docker.internal:8000/time/?start_date=${new Date(start).toISOString().split('T')[0]}`
    return msg;
}

if (end !== null) {
    msg.url = `http://host.docker.internal:8000/time/?end_date=${new Date(start).toISOString().split('T')[0]}`
    return msg;
}



return msg;

const inp = msg.payload

var descriptions = []

for (let task of inp) {
    if (task.description === null) {
        continue
    }

    if (descriptions.includes(task.description)) {
        continue
    }

    descriptions.push(task.description)
}
msg.payload = descriptions
return msg;
